import React, { Component } from 'react';
import PropTypes from 'prop-types';
import Email from "./Email"

export default class MainDisp extends Component {
    constructor(props) {
        super(props)
        this.state = {
            emailList: [],
            address: props.address,
            selectedEmail: {},
        }
    }

    makeRequest() {
        console.log("making request")
        fetch(this.state.address + "emails", { method: 'GET' })
            .then(response => {
                console.log(response); /*this is just my returned packet at this point*/
                return response /* returning response because I'm still dealing with the packet in the next block */
            })
            .then(response => {
                if (response.ok) console.log("SUCCSESS!!");
                else throw new Error("Request was bad: " + response.status);
                return response.json() /*turning to json spits out the body from the packet*/
            })
            .then(data => {
                console.log(data)/*this is now the body obj*/
                this.setState({ emailList: data })
            })
            .catch(e => console.error(e))
    }

    clickEmail(email){
        this.setState({selectedEmail: email})
    }


    //<li key={"e"+i}> {JSON.stringify(email)} </li>
    render() {
        return (<div key="mainReturn">
            <h2>--Main Disp--</h2>
            <text>Email Server: {this.state.address}</text><br />
            <button key="getButton" onClick={this.makeRequest.bind(this)}>Get Emails</button>
            <button key="back" onClick={() => { this.setState({ selectedEmail:{} }) }}> Back</button>
            <button key="hide" onClick={() => { this.setState({ emailList: [] }) }}> GoodBye</button>
            <hr />
            {this.state.emailList.map((email, i) =>
                <Email
                    onClick={()=>this.clickEmail(email)}
                    view="short"
                    email={email} />
            )}
        </div>)
    }
}

MainDisp.propTypes = {
    Truth: PropTypes.bool,
    address: PropTypes.string,

}
